import Products from "../Products/Products";
import { useState } from 'react';
import NewProduct from "../../components/NewProduct/NewProduct";
import Product from "../../components/Product/Product";

export default function Dashboard() {

    let i = 4;
    const [productsState, setProductsState] = useState(
        [
            { id: 1, name: "iPhone 13", price: 1100 },
            { id: 2, name: "iPhone 12", price: 1000 },
            { id: 3, name: "galaxy s20", price: 1050 }
        ]
    );

    const [productState, setProductState] = useState(
        {
            name: "",
            price: ""
        }
    )

    const onChange = (events) => {
        const updatedProduct = { ...productState, [events.target.name]: events.target.value };
        setProductState(updatedProduct);
    }

    const addButtonClicked = () => {
        const copy = { ...productState };
        copy.id = i;
        i++;
        const copyProductsState = [...productsState]
        copyProductsState.push(copy);
        setProductsState(copyProductsState);

    }

    const [flag, setFlag] = useState(false)
    const changeFlagHandler = () => {
        setFlag(!flag)
    }

    return (
        <div >
            {/* <Product name={productsState[0].name} price={productsState[0].price} />
            <Product name={productsState[1].name} price={productsState[1].price} /> */}
            <div className="Product">
                <Products products={productsState} />
            </div>

            <div >
                <NewProduct
                    name={productState.name}
                    price={productState.price}
                    onChange={(event) => { onChange(event) }}
                    addButtonClicked={addButtonClicked}
                />
            </div>



        </div>
    )

}
