import Posts from "../Posts/Posts";
import { useState } from 'react';
//import NewProduct from "../../components/NewProduct/NewProduct";
import Post from "../../components/Post/Post";

export default function Dashboard() {

    let i = 4;
    const [postsState, setPostsState] = useState(
        [
            { id: 1, title: "Happiness", author: "John" },
            { id: 2, title: "MIU", author: "Dean" },
            { id: 3, title: "Enjoy Life", author: "Jasmine" }
        ]
    );

    const [postState, setPostState] = useState(
        {
            title: "",
            author: ""
        }
    )

    const onChange = (events) => {
        const updatedPost = { ...postState, [events.target.name]: events.target.value };
        setPostState(updatedPost);
    }

    const addButtonClicked = () => {
        const copy = { ...postState };
        copy.id = i;
        i++;
        const copyPostsState = [...postsState]
        copyPostsState.push(copy);
        setPostsState(copyPostsState);

    }



    return (
        <div >
            {/* <Product name={productsState[0].name} price={productsState[0].price} />
            <Product name={productsState[1].name} price={productsState[1].price} /> */}
            <div className="Post">
                <Posts posts={postsState} />
            </div>

            <div >
                <NewPost
                    title={productState.name}
                    price={productState.price}
                    onChange={(event) => { onChange(event) }}
                    addButtonClicked={addButtonClicked}
                />
            </div>



        </div>
    )

}
